interface.signature:
class MeditationEntity {
  final String id;
  final String title;
  final String description;
  final int durationSeconds;
  final MeditationCategory category;
  final String audioUrl;
  final String thumbnailUrl;
  final DateTime createdAt;
  final bool isFavorited;
  final bool isDownloaded;
  final int completionCount;
}

enum MeditationCategory {
  stressRelief,
  sleep,
  focus,
  anxiety,
}

abstract class GetMeditationsUseCase {
  Future<Result<List<MeditationEntity>>> call({
    MeditationCategory? category,
    String? searchQuery,
    int? minDuration,
    int? maxDuration,
  });
}

abstract class ToggleFavoriteUseCase {
  Future<Result<bool>> call(String meditationId);
}

abstract class DownloadMeditationUseCase {
  Stream<DownloadProgress> call(String meditationId);
}

class DownloadProgress {
  final int bytesReceived;
  final int totalBytes;
  final double percentage;
  final DownloadStatus status;
}

abstract class MeditationRepository {
  Future<Result<List<MeditationEntity>>> getMeditations();
  Future<Result<MeditationEntity>> getMeditationById(String id);
  Future<Result<void>> toggleFavorite(String userId, String meditationId);
  Future<Result<List<String>>> getFavoriteIds(String userId);
  Stream<DownloadProgress> downloadAudio(String audioUrl, String localPath);
}

CREATE TABLE meditations (
  id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
  title TEXT NOT NULL,
  description TEXT NOT NULL,
  duration_seconds INT NOT NULL,
  category TEXT NOT NULL CHECK (category IN ('stress_relief', 'sleep', 'focus', 'anxiety')),
  audio_url TEXT NOT NULL,
  thumbnail_url TEXT NOT NULL,
  created_at TIMESTAMPTZ DEFAULT NOW()
);
CREATE INDEX idx_meditations_category ON meditations(category);
CREATE INDEX idx_meditations_duration ON meditations(duration_seconds);

CREATE TABLE meditation_favorites (
  id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
  user_id UUID NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,
  meditation_id UUID NOT NULL REFERENCES meditations(id) ON DELETE CASCADE,
  created_at TIMESTAMPTZ DEFAULT NOW(),
  UNIQUE(user_id, meditation_id)
);
CREATE INDEX idx_favorites_user ON meditation_favorites(user_id);

@DataClassName('MeditationDownload')
class MeditationDownloads extends Table {
  TextColumn get id => text()();
  TextColumn get userId => text()();
  TextColumn get meditationId => text()();
  TextColumn get localFilePath => text()();
  DateTimeColumn get downloadedAt => dateTime()();

  @override
  Set<Column> get primaryKey => {id};
}

CREATE TABLE meditation_sessions (
  id UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
  user_id UUID NOT NULL REFERENCES auth.users(id) ON DELETE CASCADE,
  meditation_id UUID NOT NULL REFERENCES meditations(id) ON DELETE CASCADE,
  duration_listened_seconds INT NOT NULL,
  completed BOOLEAN DEFAULT FALSE,
  completed_at TIMESTAMPTZ,
  created_at TIMESTAMPTZ DEFAULT NOW()
);
CREATE INDEX idx_sessions_user_meditation ON meditation_sessions(user_id, meditation_id);
